// src/components/BastaResultsPage.js

import React, { useState, useEffect } from 'react';

function BastaResultsPage({ playersWithIcons, onPlayAgain, onExitGameToSelector }) {
  // Inicializa los puntos de cada jugador en 0
  const initialPlayerScores = playersWithIcons.map(player => ({
    ...player,
    score: 0,
    // showProtestButton: false, // Eliminamos este estado, ya no es por jugador
  }));

  const [playerScores, setPlayerScores] = useState(initialPlayerScores);
  const [winner, setWinner] = useState(null); // Para almacenar el nombre del ganador
  const [showWinnerScreen, setShowWinnerScreen] = useState(false); // Controla la visibilidad de la pantalla del ganador

  // Función para incrementar/decrementar puntos
  const handleScoreChange = (playerId, change) => {
    setPlayerScores(prevScores =>
      prevScores.map(player =>
        player.playerId === playerId
          ? { ...player, score: Math.max(0, player.score + change) }
          : player
      )
    );
  };

  // Función para manejar el clic en el botón "Protesto" global
  const handleGlobalProtest = () => {
    // Aquí puedes definir qué pasa cuando se hace clic en el botón "Protesto" global.
    // Por ahora, solo muestra un mensaje de alerta.
    // Más adelante, esto podría abrir un modal, iniciar un proceso de votación, etc.
    alert("¡Alguien ha protestado! Es momento de revisar las respuestas.");
    console.log("Protesto general activado.");
  };

  // Manejar el clic en "Terminar" (para pasar al ganador)
  const handleFinishRound = () => {
    // Calcula el ganador
    if (playerScores.length === 0) {
      setWinner("Nadie");
    } else {
      let maxScore = -1;
      let winningPlayers = [];

      playerScores.forEach(player => {
        if (player.score > maxScore) {
          maxScore = player.score;
          winningPlayers = [player.playerId];
        } else if (player.score === maxScore) {
          winningPlayers.push(player.playerId);
        }
      });

      if (winningPlayers.length === 1) {
        setWinner(winningPlayers[0]);
      } else {
        setWinner(winningPlayers.join(" y ")); // Para múltiples ganadores
      }
    }
    setShowWinnerScreen(true);
  };

  // Renderizado de la pantalla del ganador
  if (showWinnerScreen) {
    return (
      <div className="game-container bg-white p-6 rounded-lg shadow-md text-center">
        <h2 className="text-3xl font-bold mb-6 text-gray-800">¡El ganador es...</h2>
        <p className="text-5xl font-extrabold text-purple-700 mb-8 animate-bounce">{winner}!</p>
        <div className="flex justify-center space-x-4">
          <button
            onClick={onPlayAgain}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg text-xl transition duration-300"
          >
            ¡Otra ronda!
          </button>
          <button
            onClick={onExitGameToSelector}
            className="bg-gray-600 hover:bg-gray-700 text-white font-bold py-3 px-6 rounded-lg text-xl transition duration-300"
          >
            Terminar
          </button>
        </div>
      </div>
    );
  }

  // Renderizado de la pantalla de resultados de la ronda
  return (
    <div className="game-container bg-white p-6 rounded-lg shadow-md text-center">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Resultados de la ronda</h2>

      <div className="space-y-4 mb-8">
        {playerScores.map(player => (
          <div key={player.playerId} className="flex items-center bg-gray-100 p-3 rounded-lg shadow-sm">
            <img src={player.iconUrl} alt={player.playerId} className="w-12 h-12 rounded-full mr-4 object-cover border-2 border-yellow-500" />
            <span className="flex-1 text-xl font-semibold text-gray-700 text-left">{player.playerId}</span>
            <div className="flex items-center space-x-3 ml-auto"> {/* Usamos ml-auto para empujar los botones a la derecha */}
              <button
                onClick={() => handleScoreChange(player.playerId, -25)}
                className="bg-red-400 hover:bg-red-500 text-white font-bold py-1 px-3 rounded-full text-lg disabled:opacity-50 disabled:cursor-not-allowed"
                disabled={player.score === 0}
              >
                -
              </button>
              <span className="text-3xl font-bold text-blue-700 w-16 text-center">{player.score}</span>
              <button
                onClick={() => handleScoreChange(player.playerId, 25)}
                className="bg-green-500 hover:bg-green-600 text-white font-bold py-1 px-3 rounded-full text-lg"
              >
                +
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="flex justify-center space-x-4 mt-8"> {/* Agregamos un margen superior */}
        <button
          onClick={handleGlobalProtest} // Botón "Protesto" global
          className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-3 px-6 rounded-lg text-xl transition duration-300"
        >
          Protesto
        </button>
        <button
          onClick={handleFinishRound}
          className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-3 px-6 rounded-lg text-xl transition duration-300"
        >
          Terminar Ronda
        </button>
      </div>
    </div>
  );
}

export default BastaResultsPage;
