// src/components/PirateHeader.js

import React from 'react';
import aventuraPirataLogo from '../img/Aventura Pirata icon.png'; // Ruta a tu logo

function PirateHeader() {
  return (
    // Quitamos la altura fija (h-XX) del header.
    // El header se ajustará automáticamente a la altura del contenido (la imagen) más el padding.
    // Usamos flex, items-center y justify-center para centrar la imagen.
    <header className="bg-orange-800 text-white p-4 rounded-lg shadow-md flex items-center justify-center">
      <img
        src={aventuraPirataLogo}
        alt="Aventura Pirata Logo"
        // Aumentamos el tamaño de la imagen directamente con clases como 'h-32', 'h-40', 'h-48', etc.
        // Elige la altura que desees para el logo. 'h-32' es un buen punto de partida (128px).
        // 'w-auto' mantiene las proporciones.
        // 'object-contain' asegura que la imagen se vea completa.
        className="h-48 w-auto object-contain" // Puedes cambiar 'h-32' a 'h-40', 'h-48' para que sea más grande
      />
    </header>
  );
}

export default PirateHeader;
