import { useState } from 'react';
import PirateHeader from './components/PirateHeader';
import PlayerCounter from './components/PlayerCounter';
import GameSelector from './components/GameSelector';
import GameRecommendation from './components/GameRecommendation';

const App = () => {
  const [players, setPlayers] = useState(null);
  const [gameType, setGameType] = useState(null);

  const handleBackToPlayers = () => {
    setPlayers(null);
  };

  return (
    <div className="min-h-screen bg-blue-100 p-4">
      <PirateHeader />
      <div className="max-w-md mx-auto mt-8">
        {!players ? (
          <PlayerCounter onPlayersSelected={setPlayers} />
        ) : !gameType ? (
          <GameSelector 
            onGameSelected={setGameType} 
            onBack={handleBackToPlayers}
          />
        ) : (
          <GameRecommendation 
            gameType={gameType} 
            onBack={() => setGameType(null)}
          />
        )}
      </div>
    </div>
  );
};
export default App;

