import { useState } from 'react';

const PlayerCounter = ({ onPlayersSelected }) => {
  const [totalPlayers, setTotalPlayers] = useState(2);
  const [adults, setAdults] = useState(1);
  const [children, setChildren] = useState(1);
  const [error, setError] = useState('');

  const handleChange = (type, value) => {
    setError('');
    if (type === 'total') {
      const newTotal = Math.max(1, Math.min(10, totalPlayers + value));
      setTotalPlayers(newTotal);
      setAdults(Math.min(adults, newTotal));
      setChildren(Math.min(children, newTotal));
    } else if (type === 'adults') {
      const newAdults = Math.max(0, adults + value);
      setAdults(newAdults);
    } else {
      const newChildren = Math.max(0, children + value);
      setChildren(newChildren);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (adults + children !== totalPlayers) {
      setError(`¡Faltan ${totalPlayers - (adults + children)} jugadores! Ajusta adultos o niños.`);
      return;
    }
    onPlayersSelected({ adults, children });
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md relative">
      {/* Flecha de retroceso (oculta aquí ya que es la primera pantalla) */}

      <h2 className="text-xl font-bold text-gray-800 mb-6">¿Cuántos piratas jugarán?</h2>

      {error && (
        <div className="bg-red-100 border-l-4 border-red-500 text-red-700 p-4 mb-4">
          <p>{error}</p>
        </div>
      )}

      <div className="mb-6">
        <label className="block text-gray-700 mb-3 text-center">Total de jugadores:</label>
        <div className="flex items-center justify-center">
          <button 
            onClick={() => handleChange('total', -1)}
            className="bg-red-500 text-white text-2xl w-12 h-12 rounded-full flex items-center justify-center shadow-md active:scale-95 active:bg-red-600 transition-transform"
          >
            -
          </button>
          <span className="mx-6 text-3xl font-bold min-w-[40px] text-center">{totalPlayers}</span>
          <button 
            onClick={() => handleChange('total', 1)}
            className="bg-green-500 text-white text-2xl w-12 h-12 rounded-full flex items-center justify-center shadow-md active:scale-95 active:bg-green-600 transition-transform"
          >
            +
          </button>
        </div>
      </div>

      <div className="mb-6">
        <label className="block text-gray-700 mb-3 text-center">Adultos:</label>
        <div className="flex items-center justify-center">
          <button 
            onClick={() => handleChange('adults', -1)}
            disabled={adults <= 0}
            className={`text-2xl w-12 h-12 rounded-full flex items-center justify-center shadow-md active:scale-95 transition-transform ${adults <= 0 ? 'bg-gray-300' : 'bg-blue-500 text-white active:bg-blue-600'}`}
          >
            -
          </button>
          <span className="mx-6 text-3xl font-bold min-w-[40px] text-center">{adults}</span>
          <button 
            onClick={() => handleChange('adults', 1)}
            disabled={adults >= totalPlayers - children}
            className={`text-2xl w-12 h-12 rounded-full flex items-center justify-center shadow-md active:scale-95 transition-transform ${adults >= totalPlayers - children ? 'bg-gray-300' : 'bg-blue-500 text-white active:bg-blue-600'}`}
          >
            +
          </button>
        </div>
      </div>

      <div className="mb-8">
        <label className="block text-gray-700 mb-3 text-center">Niños:</label>
        <div className="flex items-center justify-center">
          <button 
            onClick={() => handleChange('children', -1)}
            disabled={children <= 0}
            className={`text-2xl w-12 h-12 rounded-full flex items-center justify-center shadow-md active:scale-95 transition-transform ${children <= 0 ? 'bg-gray-300' : 'bg-yellow-500 text-white active:bg-yellow-600'}`}
          >
            -
          </button>
          <span className="mx-6 text-3xl font-bold min-w-[40px] text-center">{children}</span>
          <button 
            onClick={() => handleChange('children', 1)}
            disabled={children >= totalPlayers - adults}
            className={`text-2xl w-12 h-12 rounded-full flex items-center justify-center shadow-md active:scale-95 transition-transform ${children >= totalPlayers - adults ? 'bg-gray-300' : 'bg-yellow-500 text-white active:bg-yellow-600'}`}
          >
            +
          </button>
        </div>
      </div>

      <button 
        type="button" 
        onClick={handleSubmit}
        className="w-full bg-red-600 text-white py-4 px-4 rounded-xl text-xl shadow-lg active:scale-95 active:bg-red-700 transition-transform"
      >
        ¡Embarcar!
      </button>
    </div>
  );
};
export default PlayerCounter;
