import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

import PlayerCounter from './components/PlayerCounter';
import GameSelector from './components/GameSelector';
import GameRecommendation from './components/GameRecommendation'; // Todavía no hemos creado la ruta para este
import PirateHeader from './components/PirateHeader';

import './styles.css'; // Asegúrate de que tu CSS se importa aquí

// Componente Wrapper para manejar el estado y las rutas
function AppWrapper() {
  const [adultPlayers, setAdultPlayers] = useState(0);
  const [childPlayers, setChildPlayers] = useState(0);
  const [allowedDifficulties, setAllowedDifficulties] = useState([]);
  const [selectedGameType, setSelectedGameType] = useState(null); // Para la siguiente fase de GameRecommendation

  // useNavigate para la navegación programática
  const navigate = useNavigate();

  // useEffect para calcular las dificultades cuando cambian los jugadores
  useEffect(() => {
    let difficulties = [];
    if (childPlayers > 0) {
      difficulties = ['facil', 'medio'];
    } else if (adultPlayers > 0) {
      difficulties = ['medio', 'dificil'];
    }
    setAllowedDifficulties(difficulties);
    console.log("Dificultades permitidas en App.js (calculadas):", difficulties);
  }, [adultPlayers, childPlayers]);

  // Función que PlayerCounter llamará al hacer clic en "Embarcar"
  const handleEmbark = (adults, children) => {
    setAdultPlayers(adults);
    setChildPlayers(children);
    // Después de establecer los jugadores, navegamos a la siguiente ruta
    navigate('/gameSelector');
  };

  // Función para volver atrás desde GameSelector (a PlayerCounter)
  const handleBackToPlayerCounter = () => {
    navigate('/');
  };

  // Función para seleccionar el tipo de juego (se pasará a GameSelector)
  const handleSelectGameType = (gameType) => {
    setSelectedGameType(gameType);
    // Aquí puedes navegar a una ruta para GameRecommendation, por ejemplo:
    // navigate('/gameRecommendation');
  };

  // Función para volver desde GameRecommendation (a GameSelector)
  const handleBackToGameSelector = () => {
    setSelectedGameType(null); // Resetea el tipo de juego seleccionado
    navigate('/gameSelector');
  };

  return (
    <div className="min-h-screen bg-blue-100 p-4">
      <PirateHeader /> {/* El Header se mantiene intacto y siempre visible */}

      <div className="max-w-md mx-auto mt-8">
        <Routes>
          <Route path="/" element={
            // Renderiza PlayerCounter en la ruta raíz
            <PlayerCounter
              // Asumo que onPlayersChange es lo que necesitas para que PlayerCounter actualice los estados
              // y luego llame a handleEmbark.
              // Adaptaremos PlayerCounter en el siguiente paso.
              onEmbark={handleEmbark} // Pasamos la función que se ejecutará al hacer clic en "Embarcar"
              adultPlayers={adultPlayers} // Puedes pasar los estados actuales para que PlayerCounter los inicialice
              childPlayers={childPlayers}
            />
          } />

          <Route path="/gameSelector" element={
            // Renderiza GameSelector en la ruta /gameSelector
            <GameSelector
              onSelectGameType={handleSelectGameType}
              onBack={handleBackToPlayerCounter} // Le pasamos la función para la flecha de atrás
              // También puedes pasar los datos de dificultad o jugadores si los necesita aquí
              // players={{ adultPlayers, childPlayers }}
              // allowedDifficulties={allowedDifficulties}
            />
          } />

          {/* Más adelante, añadiremos la ruta para GameRecommendation */}
          <Route path="/gameRecommendation" element={
            <GameRecommendation
              gameType={selectedGameType}
              players={{ adultPlayers, childPlayers }}
              allowedDifficulties={allowedDifficulties}
              onBack={handleBackToGameSelector}
            />
          } />
        </Routes>
      </div>
    </div>
  );
}

// Envuelve AppWrapper con Router para que la navegación funcione
function App() {
  return (
    <Router>
      <AppWrapper />
    </Router>
  );
}

export default App;
