import { useState, useEffect } from 'react';
import { creativeGames, physicalGames, verbalGames } from '../mock/games';

const GameRecommendation = ({ gameType, onBack }) => {
  const [currentGame, setCurrentGame] = useState({ 
    name: '', 
    time: 0, 
    icon: '', 
    sound: '' 
  });
  const [timeLeft, setTimeLeft] = useState(0);
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [gameEnded, setGameEnded] = useState(false);
  const [gameStarted, setGameStarted] = useState(false);

  const playSound = () => {
    // Implementación real del sonido iría aquí
    console.log("🔔 Sonido de campana pirata");
  };

  const getRandomGame = () => {
    const games = {
      creative: creativeGames,
      physical: physicalGames,
      verbal: verbalGames
    };
    const selectedGames = games[gameType];
    const randomGame = selectedGames[Math.floor(Math.random() * selectedGames.length)];
    return {
      name: randomGame.name,
      time: randomGame.time * 60,
      icon: randomGame.icon,
      sound: randomGame.sound
    };
  };

  useEffect(() => {
    const newGame = getRandomGame();
    setCurrentGame(newGame);
    setTimeLeft(newGame.time);
    setIsTimerRunning(false);
    setGameEnded(false);
    setGameStarted(false);
  }, [gameType]);

  useEffect(() => {
    let timer;
    if (isTimerRunning && timeLeft > 0) {
      timer = setInterval(() => {
        setTimeLeft(prevTime => prevTime - 1);
      }, 1000);
    } else if (timeLeft === 0 && isTimerRunning) {
      setGameEnded(true);
      setIsTimerRunning(false);
      playSound();
    }
    return () => clearInterval(timer);
  }, [timeLeft, isTimerRunning]);

  const startTimer = () => {
    setIsTimerRunning(true);
    setGameStarted(true);
  };

  const getNewGame = () => {
    const newGame = getRandomGame();
    setCurrentGame(newGame);
    setTimeLeft(newGame.time);
    setIsTimerRunning(false);
    setGameEnded(false);
    setGameStarted(false);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  return (
    <div className="relative min-h-screen p-4">
      {/* Decoración exterior */}
      <div className="absolute top-20 left-4 text-7xl text-amber-300 opacity-60">🏴‍☠️</div>
      <div className="absolute bottom-20 right-4 text-7xl text-amber-300 opacity-60">⚓</div>

      {/* Recuadro principal */}
      <div className="bg-amber-50 p-6 rounded-xl border-4 border-amber-200 shadow-lg max-w-md mx-auto relative z-10">
        {/* Flecha de retroceso */}
        <button 
          onClick={onBack}
          className="absolute top-4 left-4 z-10 bg-amber-100 p-2 rounded-full shadow-md hover:bg-amber-200 transition-colors"
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-amber-800" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
          </svg>
        </button>

        {/* Contenido principal */}
        <div className="mt-12 text-center">
          <div className="text-8xl mb-4">{currentGame.icon}</div>
          <h2 className="text-3xl font-bold text-amber-800 mb-6">¡Misión Pirata!</h2>

          <div className="bg-white p-6 rounded-xl mb-6 shadow-inner border-4 border-amber-200">
            <p className="text-2xl text-amber-800 font-medium mb-2">{currentGame.name}</p>
            <p className="text-amber-600">Duración: {currentGame.time / 60} minutos</p>
          </div>

          {!gameStarted ? (
            <button 
              onClick={startTimer}
              className="w-full bg-green-600 hover:bg-green-700 text-white py-4 px-6 rounded-xl text-xl font-bold shadow-lg active:scale-95 transition-transform mb-4"
            >
              Iniciar Temporizador
            </button>
          ) : (
            <div className={`mb-6 p-4 rounded-xl ${gameEnded ? 'bg-red-100' : 'bg-blue-100'}`}>
              <div className="flex justify-center items-center">
                <span className="text-3xl mr-3">⏳</span>
                <span className="text-2xl font-bold">{formatTime(timeLeft)}</span>
              </div>
              {gameEnded && (
                <p className="text-red-600 font-bold text-2xl animate-pulse text-center mt-4">
                  ¡TIEMPO TERMINADO!
                </p>
              )}
            </div>
          )}

          <button 
            onClick={getNewGame}
            className="w-full bg-amber-600 hover:bg-amber-700 text-white py-4 px-6 rounded-xl text-xl font-bold shadow-lg active:scale-95 transition-transform"
          >
            Nuevo Juego
          </button>
        </div>
      </div>
    </div>
  );
};
export default GameRecommendation;

// DONE
