// src/components/TetrisGame.js

import React, { useState, useEffect, useRef, useCallback } from 'react';

// YA NO NECESITAMOS ESTAS IMPORTACIONES DIRECTAS
// import I_tetris from '../img/I_tetris.png';
// import L_tetris from '../img/L_tetris.png';
// import T_tetris from '../img/T_tetris.png';
// import Cube_tetris from '../img/Cube_tetris.png';
// import S_tetris from '../img/S_tetris.png';

// Y YA NO NECESITAMOS EL tetrisImageMap

// Función para obtener la URL de la imagen de forma dinámica con require()
// Esto permite a Webpack procesar la ruta si la imagen está en src/
const getImageUrl = (imagePath) => {
  try {
    // require() dinámico: asegura que Webpack procese estas rutas
    // La ruta debe ser relativa al contexto de donde se llama require.
    // Como las rutas en el JSON son "../src/img/", necesitamos ajustarlas
    // para que sean relativas a este componente (src/components/).
    // "../src/img/image.png" desde src/components/ es "../../src/img/image.png"
    // Sin embargo, si el JSON ya contiene las rutas "desde la raíz del proyecto"
    // como '../src/img/I_tetris.png', require() podría necesitar un contexto diferente.
    // La forma más robusta es que el JSON tenga las rutas exactas.

    // Si tu JSON ya tiene '../src/img/I_tetris.png', necesitamos "requerir" desde
    // la perspectiva del archivo actual.
    // Asumiendo que TetrisGame.js está en src/components/,
    // y las imágenes en src/img/, la ruta relativa correcta desde TetrisGame.js a src/img/
    // sería '../img/'.
    // Entonces, si imagePath es "../src/img/I_tetris.png", necesitamos extraer solo "I_tetris.png".
    
    const fileName = imagePath.split('/').pop(); // Extrae "I_tetris.png"
    // Y luego lo combinamos con la ruta relativa de components a img
    return require(`../img/${fileName}`);
  } catch (e) {
    console.error(`Error al cargar la imagen: ${imagePath}`, e);
    return null; // Devuelve null o una imagen de placeholder
  }
};


function TetrisGame({ players, onExitGame }) {
  const [tetrisPieces, setTetrisPieces] = useState([]);
  const [currentPiece, setCurrentPiece] = useState(null);
  const [turnTimeLeft, setTurnTimeLeft] = useState(10);
  const [showChangePlayerMessage, setShowChangePlayerMessage] = useState(false);
  const [changePlayerTimer, setChangePlayerTimer] = useState(0);
  const [isGameActive, setIsGameActive] = useState(false);
  const [turnCounter, setTurnCounter] = useState(0);

  const turnTimerRef = useRef(null);
  const changePlayerTimerRef = useRef(null);

  // Función para obtener una pieza aleatoria
  const getRandomPiece = useCallback((excludeFirstITetromino = false) => {
    if (tetrisPieces.length === 0) return null;

    let availablePieces = [...tetrisPieces];

    if (excludeFirstITetromino) {
      availablePieces = availablePieces.filter(piece => piece.nombre !== "Tetris_Oco_I");
      if (availablePieces.length === 0) {
        console.warn("No hay piezas disponibles después de excluir 'Tetris_Oco_I'.");
        return null; 
      }
    }
    
    const randomIndex = Math.floor(Math.random() * availablePieces.length);
    return availablePieces[randomIndex];
  }, [tetrisPieces]);


  // Inicia el temporizador de turno
  const startTurnTimer = useCallback(() => {
    clearInterval(turnTimerRef.current);
    setTurnTimeLeft(10);
    setIsGameActive(true);
  }, []);

  // Lógica para avanzar al siguiente turno
  const handleNextTurnLogic = useCallback(() => {
    clearInterval(turnTimerRef.current);
    
    const nextPiece = getRandomPiece(false);
    if (nextPiece) {
      setCurrentPiece(nextPiece);
      startTurnTimer();
      setIsGameActive(true);
    } else {
      console.log("No hay más piezas para jugar. Juego terminado.");
      setIsGameActive(false);
      if (onExitGame) {
        onExitGame();
      }
    }
  }, [getRandomPiece, onExitGame, startTurnTimer]);


  // Carga las piezas de tetris_data.json al inicio
  useEffect(() => {
    import('../mock/tetris_data.json')
      .then(module => {
        const data = module.default;
        setTetrisPieces(data);

        const firstPiece = getRandomPiece(true);
        if (firstPiece) {
          setCurrentPiece(firstPiece);
          startTurnTimer();
          setTurnCounter(1);
        } else {
            console.error("No se pudo iniciar el juego de Tetris. No hay piezas válidas.");
            setIsGameActive(false);
        }
      })
      .catch(error => {
        console.error("Error al cargar los datos de Tetris:", error);
        setIsGameActive(false);
      });

    return () => {
      clearInterval(turnTimerRef.current);
      clearInterval(changePlayerTimerRef.current);
    };
  }, [getRandomPiece, startTurnTimer]);

  // --- Lógica del Temporizador de Turno (10 segundos) ---
  useEffect(() => {
    if (isGameActive && turnTimeLeft > 0 && !showChangePlayerMessage) {
      turnTimerRef.current = setInterval(() => {
        setTurnTimeLeft(prevTime => prevTime - 1);
      }, 1000);
    } else if (turnTimeLeft === 0 && isGameActive) {
      clearInterval(turnTimerRef.current);
      console.log("¡Tiempo agotado para el turno!");
      setIsGameActive(false);
      setShowChangePlayerMessage(true);
      setChangePlayerTimer(7);
      setTurnCounter(prev => prev + 1);
    }
    return () => clearInterval(turnTimerRef.current);
  }, [turnTimeLeft, isGameActive, showChangePlayerMessage]);

  // --- Lógica del Temporizador de Cambio de Jugador (7 segundos) ---
  useEffect(() => {
    if (showChangePlayerMessage && changePlayerTimer > 0) {
      changePlayerTimerRef.current = setInterval(() => {
        setChangePlayerTimer(prevTime => prevTime - 1);
      }, 1000);
    } else if (changePlayerTimer === 0 && showChangePlayerMessage) {
      clearInterval(changePlayerTimerRef.current);
      setShowChangePlayerMessage(false);
      handleNextTurnLogic();
    }
    return () => clearInterval(changePlayerTimerRef.current);
  }, [showChangePlayerMessage, changePlayerTimer, handleNextTurnLogic]);


  const handleVale = () => {
    clearInterval(turnTimerRef.current);
    setIsGameActive(false);
    setShowChangePlayerMessage(true);
    setChangePlayerTimer(7);
    setTurnCounter(prev => prev + 1);
  };

  const handleTerminar = () => {
    clearInterval(turnTimerRef.current);
    clearInterval(changePlayerTimerRef.current);
    setIsGameActive(false);
    if (onExitGame) {
      onExitGame();
    }
  };

  const progressPercentage = (turnTimeLeft / 10) * 100;

  return (
    <div className="game-container bg-white p-6 rounded-lg shadow-md text-center relative">
      <button
        onClick={onExitGame}
        className="absolute top-4 left-4 bg-gray-200 hover:bg-gray-300 p-2 rounded-full text-xl"
      >
        &#8592;
      </button>

      <h2 className="text-2xl font-bold mb-4 text-gray-800">¡Tetris!</h2>
      <p className="text-lg text-gray-700 mb-4">Turno #{turnCounter}</p>


      <div className="w-full bg-gray-200 rounded-full h-4 dark:bg-gray-700 mb-6">
        <div
          className="bg-purple-500 h-4 rounded-full transition-all duration-1000 ease-linear"
          style={{ width: `${progressPercentage}%` }}
        ></div>
      </div>
      <div className="text-xl font-bold mb-8 text-purple-700">Tiempo restante: {turnTimeLeft}s</div>

      {showChangePlayerMessage ? (
        <div className="my-10">
          <p className="text-green-600 text-5xl font-bold mb-4">¡Cambia de Jugador!</p>
          <p className="text-gray-700 text-3xl">Siguiente pieza en {changePlayerTimer}s</p>
        </div>
      ) : (
        currentPiece ? (
          <>
            <div className="text-3xl font-extrabold text-blue-700 my-4">
              "{currentPiece.nombre}"
            </div>
            <div className="my-8 flex justify-center">
              <img 
                // Usamos la función getImageUrl para cargar la imagen
                src={getImageUrl(currentPiece.url_imagen)} 
                alt={currentPiece.nombre} 
                className="max-h-64 object-contain shadow-lg rounded-lg"
              />
            </div>

            <div className="flex justify-center space-x-4 mt-8">
              <button
                onClick={handleVale}
                className="bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-6 rounded-lg text-lg transition duration-300"
              >
                ¡Vale!
              </button>
              <button
                onClick={handleTerminar}
                className="bg-red-500 hover:bg-red-600 text-white font-bold py-3 px-6 rounded-lg text-lg transition duration-300"
              >
                Terminar
              </button>
            </div>
          </>
        ) : (
          <div className="text-gray-600 text-xl my-8">Cargando pieza de Tetris...</div>
        )
      )}
    </div>
  );
}

export default TetrisGame;
