// src/App.js (Ejemplo, tu App.js puede variar)

import React, { useState } from 'react';
import PlayerCounter from './components/PlayerCounter'; // Si lo usas
import GameRecommendation from './components/GameRecommendation'; // Si lo usas
import VeoVeoGame from './components/VeoVeoGame'; // Tu componente VeoVeoGame
import PictionaryGame from './components/PictionaryGame'; // <--- Importa el nuevo componente PictionaryGame

function App() {
  const [players, setPlayers] = useState({ adults: 0, kids: 0 });
  const [selectedGame, setSelectedGame] = useState(null); // 'veoVeo', 'pictionary', null
  const [allowedDifficulties, setAllowedDifficulties] = useState([]);

  const handlePlayersChange = (newPlayers) => {
    setPlayers(newPlayers);
  };

  const handleGameSelected = (gameName, difficulties) => {
    setSelectedGame(gameName);
    setAllowedDifficulties(difficulties);
  };

  const handleExitGame = () => {
    setSelectedGame(null); // Regresa a la pantalla de selección de juegos
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center py-10">
      {selectedGame === null && (
        <>
          <PlayerCounter onPlayersChange={handlePlayersChange} />
          {players.adults + players.kids > 0 && (
            <GameRecommendation
              players={players}
              onGameSelected={handleGameSelected}
            />
          )}
        </>
      )}

      {selectedGame === 'veoVeo' && (
        <VeoVeoGame
          players={players}
          allowedDifficulties={allowedDifficulties}
          onExitGame={handleExitGame}
        />
      )}

      {/* --- Agrega esta parte para Pictionary --- */}
      {selectedGame === 'pictionary' && (
        <PictionaryGame
          players={players}
          allowedDifficulties={allowedDifficulties}
          onExitGame={handleExitGame}
        />
      )}
    </div>
  );
}

export default App;
