// src/App.js

import React, { useState } from 'react';
import PlayerCounter from './components/PlayerCounter';
import GameRecommendation from './components/GameRecommendation';
import VeoVeoGame from './components/VeoVeoGame';
import PictionaryGame from './components/PictionaryGame';
import PirateHeader from './components/PirateHeader'; // <--- Importa tu componente PirateHeader

function App() {
  const [players, setPlayers] = useState({ adults: 0, kids: 0 });
  const [selectedGame, setSelectedGame] = useState(null); // 'veoVeo', 'pictionary', null
  const [allowedDifficulties, setAllowedDifficulties] = useState([]);

  const handlePlayersChange = (newPlayers) => {
    setPlayers(newPlayers);
  };

  const handleGameSelected = (gameName, difficulties) => {
    setSelectedGame(gameName);
    setAllowedDifficulties(difficulties);
  };

  const handleExitGame = () => {
    setSelectedGame(null); // Regresa a la pantalla de selección de juegos
  };

  return (
    // El div principal que envuelve toda la aplicación
    <div className="min-h-screen bg-gray-100 flex flex-col items-center">
      {/* --- EL PirateHeader SE MUESTRA SIEMPRE AQUÍ FUERA DE CUALQUIER CONDICIONAL --- */}
      <PirateHeader /> {/* <--- Coloca tu componente PirateHeader aquí */}

      {/* El contenido principal, que puede ser la selección de juegos o un juego específico */}
      {/* Ajusto un poco las clases para que el contenido principal se vea bien debajo del header */}
      <main className="flex-grow flex flex-col items-center justify-center w-full max-w-4xl px-4 py-8"> 
        {selectedGame === null && (
          <>
            <PlayerCounter onPlayersChange={handlePlayersChange} />
            {players.adults + players.kids > 0 && (
              <GameRecommendation
                players={players}
                onGameSelected={handleGameSelected}
              />
            )}
          </>
        )}

        {selectedGame === 'veoVeo' && (
          <VeoVeoGame
            players={players}
            allowedDifficulties={allowedDifficulties}
            onExitGame={handleExitGame}
          />
        )}

        {selectedGame === 'pictionary' && (
          <PictionaryGame
            players={players}
            allowedDifficulties={allowedDifficulties}
            onExitGame={handleExitGame}
          />
        )}
      </main>
    </div>
  );
}

export default App;
