// src/components/VeoVeoGame.js

import React, { useState, useEffect, useRef } from 'react';
// import Confetti from 'react-confetti'; // Descomentar si tienes instalado react-confetti

function VeoVeoGame({ players, allowedDifficulties, onExitGame }) {
  // Aqu√≠ almacenar√≠amos las pistas filtradas del juego Veo Veo
  const [gameData, setGameData] = useState([]);
  const [currentObjectIndex, setCurrentObjectIndex] = useState(0); // √çndice del objeto actual (para 5 objetos)
  const [currentPistaIndex, setCurrentPistaIndex] = useState(0); // √çndice de la pista actual del objeto
  const [timeLeft, setTimeLeft] = useState(25); // Tiempo restante para el temporizador principal
  const [isGameActive, setIsGameActive] = useState(false); // Si el juego est√° en curso
  const [showConfetti, setShowConfetti] = useState(false); // Para la animaci√≥n de confeti
  const [nextObjectTimer, setNextObjectTimer] = useState(0); // Temporizador para el siguiente objeto (5 segundos)

  const timerRef = useRef(null); // Para controlar el temporizador principal
  const nextObjectTimerRef = useRef(null); // Para controlar el temporizador del siguiente objeto

  // Funci√≥n para cargar y filtrar las pistas seg√∫n la dificultad
  useEffect(() => {
    const veoveoData = require('../mock/veoveo_data.json');

    // ¬°CAMBIO CLAVE AQU√ç PARA EL FILTRADO DE DIFICULTAD!
    const filteredData = veoveoData.filter(item =>
      // Comprobamos si item.Dificultad existe y si est√° incluida en allowedDifficulties
      // Usamos el m√©todo .includes() en allowedDifficulties (que es un array)
      // para ver si el string de dificultad del item est√° en la lista de dificultades permitidas.
      item.Dificultad && allowedDifficulties.includes(item.Dificultad)
    );

    // Mapeamos los datos filtrados para usar claves en min√∫sculas (respuesta, pistas)
    // para que coincidan con el resto del componente.
    const mappedData = filteredData.map(item => ({
      respuesta: item.Objeto, // Mapeamos "Objeto" a "respuesta"
      pistas: item.Pistas,    // Mapeamos "Pistas" a "pistas"
      dificultad: item.Dificultad // Mantenemos la dificultad original si la necesitamos para debug
    }));

    // Seleccionar 5 objetos al azar de los filtrados
    const selectedObjects = [];
    if (mappedData.length > 0) {
      const shuffledData = [...mappedData].sort(() => 0.5 - Math.random()); // Mezclar
      for (let i = 0; i < 5 && i < shuffledData.length; i++) {
        selectedObjects.push(shuffledData[i]);
      }
    }
    setGameData(selectedObjects);

    console.log("Datos de Veo Veo filtrados por dificultad:", selectedObjects);
    // Reiniciar el juego si los datos cambian (solo si se vuelve a montar el componente)
    if (selectedObjects.length > 0) {
      setCurrentObjectIndex(0);
      setCurrentPistaIndex(0);
      setTimeLeft(25);
      setIsGameActive(true);
      // Iniciar el temporizador principal solo si el juego est√° activo y no hay un temporizador de "siguiente objeto"
      if (!nextObjectTimerRef.current) {
         startMainTimer();
      }
    } else {
      setIsGameActive(false);
      console.log("No hay objetos de Veo Veo para la dificultad seleccionada.");
    }

    // Limpieza al desmontar el componente o al cambiar la dependencia
    return () => {
      clearInterval(timerRef.current);
      clearInterval(nextObjectTimerRef.current);
    };
  }, [allowedDifficulties]); // Se ejecuta cuando las dificultades permitidas cambian


  // useEffect para manejar el temporizador principal (25 segundos por objeto)
  useEffect(() => {
    if (isGameActive && timeLeft > 0 && nextObjectTimer === 0) {
      timerRef.current = setInterval(() => {
        setTimeLeft(prevTime => prevTime - 1);
      }, 1000);
    } else if (timeLeft === 0 && isGameActive) {
      clearInterval(timerRef.current);
      // Se acab√≥ el tiempo para este objeto
      console.log("¬°Se acab√≥ el tiempo! El objeto era:", gameData[currentObjectIndex]?.respuesta);
      startNextObjectTimer(true); // Inicia el temporizador de 5 segundos, indicando que fue por tiempo agotado
      setIsGameActive(false); // Pausa el juego principal
    }

    return () => clearInterval(timerRef.current);
  }, [timeLeft, isGameActive, nextObjectTimer, currentObjectIndex, gameData]); // Agregu√© currentObjectIndex y gameData como dependencias.


  // Funci√≥n para iniciar el temporizador principal (25 segundos)
  const startMainTimer = () => {
    clearInterval(timerRef.current); // Asegura que no haya m√∫ltiples temporizadores
    setTimeLeft(25);
    setIsGameActive(true);
    setNextObjectTimer(0); // Asegurarse de que el temporizador de "siguiente objeto" est√© en 0
  };

  // Funci√≥n para iniciar el temporizador de 5 segundos para el siguiente objeto
  const startNextObjectTimer = (timeUp = false) => {
    setShowConfetti(false); // Ocultar confeti
    setNextObjectTimer(5);
    clearInterval(nextObjectTimerRef.current); // Limpia cualquier temporizador previo
    clearInterval(timerRef.current); // Aseg√∫rate de detener el temporizador principal tambi√©n

    nextObjectTimerRef.current = setInterval(() => {
      setNextObjectTimer(prevTime => {
        if (prevTime <= 1) {
          clearInterval(nextObjectTimerRef.current);
          handleNextObject(); // Avanza al siguiente objeto
          return 0;
        }
        return prevTime - 1;
      });
    }, 1000);
  };

  // Funci√≥n para avanzar al siguiente objeto
  const handleNextObject = () => {
    // Comprobar si quedan m√°s objetos (hasta 5)
    if (currentObjectIndex < 4 && gameData.length > (currentObjectIndex + 1)) {
      setCurrentObjectIndex(prevIndex => prevIndex + 1);
      setCurrentPistaIndex(0); // Reiniciar pistas para el nuevo objeto
      startMainTimer(); // Iniciar temporizador para el nuevo objeto
    } else {
      // Si no quedan m√°s objetos, el juego termina
      console.log("¬°Juego de Veo Veo Terminado!");
      setIsGameActive(false);
      clearInterval(timerRef.current);
      clearInterval(nextObjectTimerRef.current);
      if (onExitGame) {
        onExitGame(); // Vuelve al GameSelector
      }
    }
  };

  // Funci√≥n para el bot√≥n "Adivin√©"
  const handleAdivine = () => {
    clearInterval(timerRef.current); // Detener el temporizador principal
    setIsGameActive(false); // Pausar el juego principal
    setShowConfetti(true); // Mostrar confeti

    console.log("¬°Adivin√≥ el objeto:", gameData[currentObjectIndex]?.respuesta);
    startNextObjectTimer(); // Inicia el temporizador de 5 segundos para el siguiente objeto
  };

  // Funci√≥n para el bot√≥n "Otra Pista"
  const handleOtraPista = () => {
    const currentObject = gameData[currentObjectIndex];
    if (currentObject && currentPistaIndex < currentObject.pistas.length - 1) {
      setCurrentPistaIndex(prevIndex => prevIndex + 1);
    } else {
      console.log("No hay m√°s pistas para este objeto.");
      // Si no hay m√°s pistas, podr√≠amos avanzar al siguiente objeto o mostrar la respuesta.
      // Por ahora, si no hay m√°s pistas, se asume que el usuario debe adivinar o el tiempo se agota.
    }
  };

  // Muestra "Cargando..." o similar si no hay datos filtrados o el juego no ha comenzado
  if (!isGameActive && gameData.length === 0) {
    return (
      <div className="max-w-md mx-auto mt-8 bg-white p-6 rounded-lg shadow-md text-center">
        <h2 className="text-2xl font-bold mb-4 text-gray-800">Cargando juego Veo Veo...</h2>
        <p className="text-gray-600">Aseg√∫rate de que hay datos de Veo Veo para las dificultades seleccionadas.</p>
        <button onClick={onExitGame} className="mt-4 bg-red-500 hover:bg-red-600 text-white font-bold py-2 px-4 rounded">
          Salir del Juego
        </button>
      </div>
    );
  }

  // Si no hay objeto actual (por ejemplo, juego terminado o error)
  if (!gameData[currentObjectIndex]) {
    return (
      <div className="max-w-md mx-auto mt-8 bg-white p-6 rounded-lg shadow-md text-center">
        <h2 className="text-2xl font-bold mb-4 text-gray-800">Juego Veo Veo Terminado</h2>
        <p className="text-gray-600">Gracias por jugar. ¬°Vuelve pronto!</p>
        <button onClick={onExitGame} className="mt-4 bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded">
          Volver al Selector de Juego
        </button>
      </div>
    );
  }

  // Renderizado principal del juego
  return (
    <div className="game-container bg-white p-6 rounded-lg shadow-md text-center relative">
      {showConfetti && (
        // <Confetti
        //   width={window.innerWidth}
        //   height={window.innerHeight}
        //   recycle={false} // Para que se dispare una sola vez
        //   numberOfPieces={200}
        //   gravity={0.1}
        // />
        <div className="confetti-placeholder text-4xl mb-4">üéâ Confeti! üéâ</div> // Placeholder visual
      )}

      {/* Bot√≥n de Salir del Juego */}
      <button
        onClick={onExitGame}
        className="absolute top-4 left-4 bg-gray-200 hover:bg-gray-300 p-2 rounded-full text-xl"
      >
        &#8592; {/* Flecha de atr√°s */}
      </button>

      <h2 className="text-2xl font-bold mb-4 text-gray-800">¬°Veo Veo!</h2>

      {/* Contador de objeto actual / total de objetos */}
      <p className="text-lg text-gray-700 mb-2">Objeto {currentObjectIndex + 1} de {gameData.length}</p>

      {/* Temporizador principal */}
      <div className="text-xl font-bold mb-4">Tiempo restante: <span className="text-red-500">{timeLeft}s</span></div>

      {/* Pista actual */}
      <div className="text-4xl font-extrabold text-blue-600 my-8">
        {nextObjectTimer > 0 ? ( // Si el temporizador de siguiente objeto est√° activo
          showConfetti ? (
            <p className="text-green-600">¬°Correcto! Siguiente objeto en {nextObjectTimer}s</p>
          ) : (
            <p className="text-red-600">¬°Se acab√≥ el tiempo! Era: "{gameData[currentObjectIndex]?.respuesta}". Siguiente en {nextObjectTimer}s</p>
          )
        ) : ( // Si el temporizador de siguiente objeto no est√° activo
          <p className="text-blue-600">"{gameData[currentObjectIndex]?.pistas[currentPistaIndex]}"</p>
        )}
      </div>

      {/* Botones de acci√≥n */}
      <div className="flex justify-center space-x-4 mt-8">
        {isGameActive && nextObjectTimer === 0 && ( // Solo muestra botones si el juego est√° activo y no es tiempo de espera
          <>
            <button
              onClick={handleAdivine}
              className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-3 px-6 rounded-lg text-lg transition duration-300"
            >
              ¬°Adivin√©!
            </button>
            <button
              onClick={handleOtraPista}
              className="bg-yellow-600 hover:bg-yellow-700 text-white font-bold py-3 px-6 rounded-lg text-lg transition duration-300"
            >
              Otra Pista
            </button>
          </>
        )}
      </div>
    </div>
  );
}

export default VeoVeoGame;
