import React, { useState } from 'react';

const playersMock = [
  { playerId: 'Manuel', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Mario', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Mayra', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Raul', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Luisa', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Diego', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Lorena', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Juan', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Ivanna', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Cristian', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Adrian', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Azul', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Daniela', iconUrl: 'https://via.placeholder.com/64' },
  { playerId: 'Alonso', iconUrl: 'https://via.placeholder.com/64' },
];

function Grid5xN() {
  const [scores, setScores] = useState(playersMock.map(p => ({ ...p, score: 0 })));

  const updateScore = (id, delta) => {
    setScores(prev =>
      prev.map(p =>
        p.playerId === id ? { ...p, score: Math.max(0, p.score + delta) } : p
      )
    );
  };

  return (
    <div className="min-h-screen flex justify-center items-start bg-blue-100 py-10">
      <div className="bg-white rounded-lg shadow-lg p-6">
        <h2 className="text-2xl font-bold text-center mb-6">Resultados de la ronda</h2>

        {/* GRID DE 5 COLUMNAS FIJAS */}
        <div className="grid grid-cols-5 gap-4">
          {scores.map(player => (
            <div
              key={player.playerId}
              className="bg-gray-100 p-4 rounded-lg shadow flex flex-col items-center min-h-[250px]"
            >
              <img
                src={player.iconUrl}
                alt={player.playerId}
                className="w-16 h-16 rounded-full mb-2 object-cover border-2 border-yellow-500"
              />
              <p className="text-center font-semibold text-gray-800 mb-2 break-words leading-tight">
                {player.playerId}
              </p>
              <div className="flex items-center mt-auto gap-2">
                <button
                  onClick={() => updateScore(player.playerId, -25)}
                  className="bg-red-400 hover:bg-red-500 text-white px-3 py-1 rounded-full text-lg disabled:opacity-50"
                  disabled={player.score === 0}
                >
                  -
                </button>
                <span className="text-xl font-bold text-blue-700 w-8 text-center">{player.score}</span>
                <button
                  onClick={() => updateScore(player.playerId, 25)}
                  className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded-full text-lg"
                >
                  +
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* BOTÃ“N FINAL */}
        <div className="flex justify-center mt-8">
          <button className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-6 rounded-lg text-lg">
            Terminar Ronda
          </button>
        </div>
      </div>
    </div>
  );
}

export default Grid5xN;
