// src/components/PirateHeader.js

import React from 'react';
// ¡Solo este import!
import aventuraPirataLogo from '../img/Aventura Pirata icon.png'; // Asegúrate de que esta ruta sea la correcta (src/img/)

function PirateHeader() {
  return (
    // He puesto una altura fija h-40 al header para que el logo tenga espacio para crecer.
    // flex, items-center y justify-center para centrar el logo perfectamente.
    <header className="bg-orange-800 text-white p-4 rounded-lg shadow-md flex items-center justify-center h-40"> {/* Puedes ajustar h-40 a h-32, h-48, etc., según la altura que desees para el header */}
      <img
        src={aventuraPirataLogo}
        alt="Aventura Pirata Logo"
        // w-auto y h-full para que el ancho se ajuste y ocupe toda la altura del header
        // object-contain para asegurar que la imagen se vea completa dentro del espacio
        className="h-full w-auto object-contain"
      />
    </header>
  );
}

export default PirateHeader;
