// src/components/PirateHeader.js

import React from 'react';
import aventuraPirataLogo from '../img/Aventura Pirata icon.png'; // Ruta a tu logo

function PirateHeader() {
  return (
    // Paso 1: Establecemos una altura fija para el header.
    // Usaremos 'h-40' (160px) como ejemplo, pero puedes ajustar este valor.
    // 'overflow-hidden' es crucial para asegurar que si la imagen es m치s grande,
    // el contenido que exceda la altura del header se recorte en lugar de estirarlo.
    <header className="bg-orange-800 text-white p-4 rounded-lg shadow-md flex items-center justify-center h-40 overflow-hidden">
      <img
        src={aventuraPirataLogo}
        alt="Aventura Pirata Logo"
        // Paso 2: La imagen debe ocupar el 100% de la altura de su contenedor padre (`header`).
        // 'w-auto' para mantener las proporciones.
        // 'object-contain' si quieres que la imagen se escale para caber completamente (puede dejar espacio lateral).
        // 'object-cover' si quieres que la imagen cubra todo el espacio, recort치ndose si es necesario.
        // Dada la forma del logo, 'object-contain' es probablemente lo que quieres para que no se corte.
        className="h-full w-auto object-contain"
      />
    </header>
  );
}

export default PirateHeader;
