// src/components/PirateHeader.js

import React from 'react';
// Asegúrate de que las rutas y los nombres de archivo sean correctos
import aventuraPirataLogo from '../img/Aventura Pirata icon.png'; // Tu nuevo logo
import skullFlag from '../img/skull_flag.png';   // Suponiendo que tienes un icono de bandera pirata en src/img

function PirateHeader() {
  return (
    // Hemos añadido una altura fija (ej. h-32 o h-40) al header.
    // También flex-row para que los elementos se distribuyan horizontalmente
    // y justify-between para empujar el ancla a la izquierda y la bandera a la derecha.
    <header className="bg-orange-800 text-white p-4 rounded-lg shadow-md flex items-center justify-between h-40"> {/* h-40 = 160px de alto */}

      {/* Icono del ancla a la izquierda */}
      <img src={anchorIcon} alt="Ancla pirata" className="h-full w-auto object-contain mr-4" />

      {/* Contenedor central para el logo "Aventura Pirata" */}
      <div className="flex-1 flex items-center justify-center h-full"> {/* Este div ocupará el espacio restante y centrará el logo */}
        <img
          src={aventuraPirataLogo}
          alt="Aventura Pirata Logo"
          // CLASES MODIFICADAS AQUÍ PARA EL LOGO PRINCIPAL:
          // h-full hará que ocupe el 100% de la altura de su contenedor padre (el div flex-1)
          // w-auto para mantener proporciones
          // object-contain para asegurar que se vea completo
          className="h-full w-auto object-contain"
        />
      </div>

      {/* Icono de la bandera pirata a la derecha */}
      <img src={skullFlag} alt="Bandera pirata" className="h-full w-auto object-contain ml-4" />

    </header>
  );
}

export default PirateHeader;
