// src/components/BastaResultsPage.js

import React, { useState } from 'react';
import ProtestModal from './ProtestModal';

function BastaResultsPage({ playersWithIcons, onPlayAgain, onExitGameToSelector }) {
  const initialPlayerScores = playersWithIcons.map(player => ({
    ...player,
    score: 0,
  }));

  const [playerScores, setPlayerScores] = useState(initialPlayerScores);
  const [winner, setWinner] = useState(null);
  const [showWinnerScreen, setShowWinnerScreen] = useState(false);
  const [isProtestModalOpen, setIsProtestModalOpen] = useState(false);

  async function getWordDefinition(word) {
      console.log(`Buscando definición para: ${word}`);
      try {
          const response = await fetch(`https://rae-api.com/api/words/${word}`);
          if (!response.ok) {
              throw new Error(`HTTP error! status: ${response.status}`);
          }
          const data = await response.json();
          console.log(`Resultado de la RAE para "${word}":`, data);

          if (Array.isArray(data) && data.length === 0) {
              console.log(`No se encontraron definiciones para "${word}".`);
              alert(`No se encontraron definiciones para "${word}".`);
          } else if (data && data.definitions) {
              console.log(`Definiciones de "${word}":`, data.definitions);
              alert(`Palabra "${word}" encontrada. Ve la consola para los detalles.`);
          } else {
              console.log(`Respuesta inesperada de la API para "${word}":`, data);
              alert(`Respuesta inesperada para "${word}". Ve la consola para los detalles.`);
          }

      } catch (error) {
          console.error(`Error al buscar la palabra "${word}":`, error);
          alert(`Error al buscar la palabra "${word}". Revisa la consola.`);
      }
  }


  const handleScoreChange = (playerId, change) => {
    setPlayerScores(prevScores =>
      prevScores.map(player =>
        player.playerId === playerId
          ? { ...player, score: Math.max(0, player.score + change) }
          : player
      )
    );
  };

  const handleOpenProtestModal = () => {
    setIsProtestModalOpen(true);
  };

  const handleCloseProtestModal = () => {
    setIsProtestModalOpen(false);
  };

  const handleFinishRound = () => {
    if (playerScores.length === 0) {
      setWinner("Nadie");
    } else {
      let maxScore = -1;
      let winningPlayers = [];

      playerScores.forEach(player => {
        if (player.score > maxScore) {
          maxScore = player.score;
          winningPlayers = [player.playerId];
        } else if (player.score === maxScore) {
          winningPlayers.push(player.playerId);
        }
      });

      if (winningPlayers.length === 1) {
        setWinner(winningPlayers[0]);
      } else {
        setWinner(winningPlayers.join(" y "));
      }
    }
    setShowWinnerScreen(true);
  };

  if (showWinnerScreen) {
    return (
      <div className="game-container bg-white p-6 rounded-lg shadow-md text-center">
        <h2 className="text-3xl font-bold mb-6 text-gray-800">¡El ganador es...</h2>
        <p className="text-5xl font-extrabold text-purple-700 mb-8 animate-bounce">{winner}!</p>
        <div className="flex justify-center space-x-4">
          <button
            onClick={onPlayAgain}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg text-xl transition duration-300"
          >
            ¡Otra ronda!
          </button>
          <button
            onClick={onExitGameToSelector}
            className="bg-gray-600 hover:bg-gray-700 text-white font-bold py-3 px-6 rounded-lg text-xl transition duration-300"
          >
            Terminar
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="game-container bg-white p-6 rounded-lg shadow-md text-center">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Resultados de la ronda</h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-8">
        {playerScores.map(player => (
          <div key={player.playerId} className="bg-gray-100 p-4 rounded-lg shadow-sm flex flex-col items-center">
            <img src={player.iconUrl} alt={player.playerId} className="w-16 h-16 rounded-full mb-3 object-cover border-2 border-yellow-500" />
            <span className="text-xl font-semibold text-gray-800 mb-2">{player.playerId}</span>
            <div className="flex items-center space-x-3">
              <button
                onClick={() => handleScoreChange(player.playerId, -25)}
                className="bg-red-400 hover:bg-red-500 text-white font-bold py-1 px-3 rounded-full text-lg disabled:opacity-50 disabled:cursor-not-allowed"
                disabled={player.score === 0}
              >
                -
              </button>
              <span className="text-3xl font-bold text-blue-700 w-16 text-center">{player.score}</span>
              <button
                onClick={() => handleScoreChange(player.playerId, 25)}
                className="bg-green-500 hover:bg-green-600 text-white font-bold py-1 px-3 rounded-full text-lg"
              >
                +
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="flex justify-center space-x-4 mt-8">
        <button
          onClick={handleOpenProtestModal} // <--- CAMBIO AQUÍ: Moví el comentario a una línea separada
          className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-3 px-6 rounded-lg text-xl transition duration-300"
        >
          Protesto
        </button>
        <button
          onClick={handleFinishRound}
          className="bg-purple-600 hover:bg-purple-700 text-white font-bold py-3 px-6 rounded-lg text-xl transition duration-300"
        >
          Terminar Ronda
        </button>
      </div>

      {isProtestModalOpen && (
        <ProtestModal
          onClose={handleCloseProtestModal}
          onSearchWord={getWordDefinition}
        />
      )}
    </div>
  );
}

export default BastaResultsPage;
