// src/App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';

import PlayerCounter from './components/PlayerCounter';
import GameSelector from './components/GameSelector';
import GameRecommendation from './components/GameRecommendation';
import PirateHeader from './components/PirateHeader';
// Importamos el nuevo componente del juego Veo Veo
import VeoVeoGame from './components/VeoVeoGame'; 

import './styles.css';

function AppWrapper() {
  const [adultPlayers, setAdultPlayers] = useState(0);
  const [childPlayers, setChildPlayers] = useState(0);
  const [allowedDifficulties, setAllowedDifficulties] = useState([]);
  const [selectedGameType, setSelectedGameType] = useState(null);
  const [selectedGameName, setSelectedGameName] = useState(null); // Nuevo estado para el nombre del juego real

  const navigate = useNavigate();

  // useEffect para calcular las dificultades cuando cambian los jugadores
  useEffect(() => {
    let difficulties = [];
    if (childPlayers > 0) {
      difficulties = ['facil', 'medio'];
    } else if (adultPlayers > 0) {
      difficulties = ['medio', 'dificil'];
    }
    setAllowedDifficulties(difficulties);
    console.log("Dificultades permitidas en App.js (calculadas):", difficulties);
  }, [adultPlayers, childPlayers]);

  const handleEmbark = (adults, children) => {
    setAdultPlayers(adults);
    setChildPlayers(children);
    navigate('/gameSelector');
  };

  const handleBackToPlayerCounter = () => {
    navigate('/');
  };

  // Esta función es llamada desde GameSelector
  const handleSelectGameType = (gameType) => {
    setSelectedGameType(gameType);
    navigate('/gameRecommendation');
  };

  // Esta función es llamada desde GameRecommendation cuando se hace clic en "Comenzar el Juego"
  const handleStartRecommendedGame = (gameName) => {
    setSelectedGameName(gameName); // Guardamos el nombre del juego para la navegación
    // Aquí es donde navegamos al componente de juego específico
    if (gameName === 'Veo Veo') {
      navigate('/play/veo-veo'); // Nueva ruta específica para Veo Veo
    } else if (gameName === 'Pictionary') {
      navigate('/play/pictionary'); // Ruta para Pictionary (aún no implementado)
    }
    // Añadir más condiciones para otros juegos si es necesario
  };

  // Función para volver desde GameRecommendation (a GameSelector)
  const handleBackToGameSelector = () => {
    setSelectedGameType(null);
    setSelectedGameName(null); // Resetea el nombre del juego
    navigate('/gameSelector');
  };

  // Función para volver de un juego (ej. VeoVeoGame) al GameSelector
  const handleExitGame = () => {
    setSelectedGameType(null);
    setSelectedGameName(null);
    navigate('/gameSelector');
  };

  return (
    <div className="min-h-screen bg-blue-100 p-4">
      <PirateHeader />

      <div className="max-w-md mx-auto mt-8">
        <Routes>
          <Route path="/" element={
            <PlayerCounter
              onEmbark={handleEmbark}
              adultPlayers={adultPlayers}
              childPlayers={childPlayers}
            />
          } />

          <Route path="/gameSelector" element={
            <GameSelector
              onSelectGameType={handleSelectGameType}
              onBack={handleBackToPlayerCounter}
            />
          } />

          <Route path="/gameRecommendation" element={
            <GameRecommendation
              gameType={selectedGameType}
              players={{ adultPlayers, childPlayers }}
              allowedDifficulties={allowedDifficulties}
              onBack={handleBackToGameSelector}
              onStartGame={handleStartRecommendedGame} // Pasamos la nueva función para iniciar el juego real
            />
          } />

          {/* NUEVA RUTA PARA EL JUEGO VEO VEO */}
          <Route path="/play/veo-veo" element={
            <VeoVeoGame
              players={{ adultPlayers, childPlayers }}
              allowedDifficulties={allowedDifficulties}
              onExitGame={handleExitGame} // Función para volver a GameSelector al finalizar el juego
            />
          } />

          {/* Futuras rutas para otros juegos, por ejemplo: */}
          { <Route path="/play/pictionary" element={
            <PictionaryGame
              players={{ adultPlayers, childPlayers }}
              allowedDifficulties={allowedDifficulties}
              onExitGame={handleExitGame}
            />
          } />}

        </Routes>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppWrapper />
    </Router>
  );
}

export default App;
