// src/components/PirateHeader.js

import React from 'react';
import aventuraPirataLogo from '../img/Aventura Pirata icon.png'; // O la ruta que sea correcta ahora

function PirateHeader() {
  return (
    // Asegúrate de que el header tenga una altura definida o se ajuste al contenido
    // Añadí 'h-32' o 'h-40' al header si quieres un header más alto para que el logo se expanda.
    // O si quieres que el header se ajuste a la altura del logo, puedes remover las alturas fijas.
    <header className="bg-orange-800 text-white p-4 rounded-lg shadow-md flex items-center justify-center">
      <img
        src={aventuraPirataLogo}
        alt="Aventura Pirata Logo"
        // CLASES MODIFICADAS AQUÍ:
        // h-full hará que ocupe el 100% de la altura del contenedor padre (el header)
        // w-auto para que el ancho se ajuste proporcionalmente
        // mx-auto para centrar horizontalmente si el contenedor es más ancho
        className="h-full w-auto object-contain mx-auto"
        style={{ maxHeight: '100%' }} // Esto es un fallback para asegurar que no desborde
      />
    </header>
  );
}

export default PirateHeader;
