import { useState } from 'react';

const GameSelector = ({ onGameSelected, onBack }) => {
  const [gameType, setGameType] = useState('');

  const gameTypes = [
    { id: 'creative', name: 'Juegos Creativos', icon: 'üé®', color: 'bg-purple-100', hover: 'hover:bg-purple-200' },
    { id: 'physical', name: 'Juegos F√≠sicos', icon: '‚öîÔ∏è', color: 'bg-red-100', hover: 'hover:bg-red-200' },
    { id: 'verbal', name: 'Juegos Verbales', icon: 'üó£Ô∏è', color: 'bg-blue-100', hover: 'hover:bg-blue-200' }
  ];

  return (
    <div className="bg-white p-6 rounded-xl shadow-md mt-6 relative">
      {/* Flecha de retroceso */}
      <button 
        onClick={onBack}
        className="absolute top-4 left-4 z-10 bg-amber-100 p-2 rounded-full shadow-md hover:bg-amber-200 transition-colors"
      >
        <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-amber-800" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
        </svg>
      </button>

      <h2 className="text-2xl font-bold text-amber-800 mb-8 text-center">Elige tu tipo de batalla</h2>

      <div className="grid grid-cols-1 gap-4">
        {gameTypes.map((type) => (
          <button
            key={type.id}
            onClick={() => {
              setGameType(type.id);
              onGameSelected(type.id);
            }}
            className={`p-6 rounded-xl border-2 text-xl flex items-center justify-center transition-all ${gameType === type.id ? 
              'border-amber-400 scale-95 ' + type.color : 
              'border-gray-200 active:scale-95 ' + type.color + ' ' + type.hover}`}
          >
            <span className="text-4xl mr-4">{type.icon}</span>
            <span className="font-medium">{type.name}</span>
          </button>
        ))}
      </div>
    </div>
  );
};
export default GameSelector;
